import "../App.css";
import { useState } from "react";

import GridCell from "./GridCell";

const Seq = ({gridChange, gridState, playBd, playSn, playCh, playOh, playCy, defaultPattern}) => {
  /*const [gridState, setGridState] = useState(defaultPattern);
  const { bPat, sPat, hPat, oPat, cPat } = defaultPattern;
  
  const gridChange = (id) => {
    const { bPat, sPat, hPat, oPat, cPat } = gridState;
    if (id < 16) {
      bPat[id] = +!bPat[id];
      setGridState({ bPat, sPat, hPat, oPat, cPat });
      console.log(gridState);
    }
    if (id < 32 && id >= 16) {
      sPat[id - 16] = +!sPat[id - 16];
      setGridState({ bPat, sPat, hPat, oPat, cPat });
    }
    if (id < 48 && id >= 32) {
      hPat[id - 32] = +!hPat[id - 32];
      setGridState({ bPat, sPat, hPat, oPat, cPat });
    }
    if (id < 64 && id >= 48) {
      oPat[id - 48] = +!oPat[id - 48];
      setGridState({ bPat, sPat, hPat, oPat, cPat });
    }
    if (id < 80 && id >= 64) {
      cPat[id - 64] = +!cPat[id - 64];
      setGridState({ bPat, sPat, hPat, oPat, cPat });
    }
  }; */
  const { bPat, sPat, hPat, oPat, cPat } = gridState;

  const bGrid = bPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          active={false}
          key={index}
          id={index}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell active={true} key={index} id={index} gridChange={gridChange} />
    );
  });
  const sGrid = sPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          active={false}
          key={index}
          id={index + 16}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        active={true}
        key={index}
        id={index + 16}
        gridChange={gridChange}
      />
    );
  });
  const hGrid = hPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          active={false}
          key={index}
          id={index + 32}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        active={true}
        key={index}
        id={index + 32}
        gridChange={gridChange}
      />
    );
  });
  const oGrid = oPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          active={false}
          key={index}
          id={index + 48}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        active={true}
        key={index}
        id={index + 48}
        gridChange={gridChange}
      />
    );
  });
  const cGrid = cPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          active={false}
          key={index}
          id={index + 64}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        active={true}
        key={index}
        id={index + 64}
        gridChange={gridChange}
      />
    );
  });

  return (
    <div className="seq">
      <div className="seq-window">
        <p>seq</p>
        <hr />
        <div className="grid">
          {bGrid}
          {sGrid}
          {hGrid}
          {oGrid}
          {cGrid}
        </div>
      </div>
      <div className="pads">
        <div onMouseDown={playBd}>
          <p>Kick</p>
        </div>
        <div onMouseDown={playSn}>
          <p>Snare</p>
        </div>
        <div onMouseDown={playCh}>
          <p>ClHat</p>
        </div>
        <div onMouseDown={playOh}>
          <p>OpHat</p>
        </div>
        <div onMouseDown={playCy}>
          <p>Cym</p>
        </div>
      </div>
    </div>
  );
};

export default Seq;
