import "../App.css";
import Knob from "react-simple-knob";
import { useState } from "react";

const idMap = {
  0: "Kick",
  1: "Snare",
  2: "ClHat",
  3: "OpHat",
  4: "Cymbal",
}
const knobStyle = {
  margin: "0 auto",
  height: "50px",
  fontFamily: "Arial",
  color: "black",
};

const InstrumentControl = ({ id, volumeHandler, muteHandler, unmuteHandler }) => {
  const [mutedState, setMutedState] = useState(false);
  const volumeRotary = (level) => {
     volumeHandler(level, id);
  }
  const muteSet = () => {
    if (!mutedState) {
    setMutedState(true)
    muteHandler(id);
    }
    if (mutedState) {
      setMutedState(false)
      unmuteHandler(id);
    }
  }
  return (
    <div className="knobs">
      <p>{id}</p>
      <button onClick={muteSet}>Mute</button>
      <button>Solo</button>
      <Knob
        name="Volume"
        unit=""
        defaultPercentage={0}
        onChange={volumeRotary}
        bg="black"
        fg="white"
        mouseSpeed={1}
        transform={(p) => parseInt(p * -24)}
        style={knobStyle}
      />
    </div>
  );
};

export default InstrumentControl;
