import "../App.css";
import InstrumentControl from "./InstrumentControl";
import Master from "./Master";
import Dropdown from "../UI/Dropdown";

const Header = ({
  setKit,
  setVolume,
  setDist,
  setRev,
  setBpm,
  setFilter,
  start,
  stop,
}) => {
  return (
    <div className="header">
      <div className="sub-header">
        <div className="info">
          <span>Header/info</span>
          <div className="display">
          <Dropdown
          onChange={(value) => setKit(value)}
          placeHolder="Select..."
          isMulti={false}
        />
          </div>
        </div>
        <div className="inst-sect">
          <InstrumentControl id={0} />
          <InstrumentControl id={1} />
          <InstrumentControl id={2} />
          <InstrumentControl id={3} />
          <InstrumentControl id={4} />
        </div>
      </div>
      <Master setDist={setDist} setFilter={setFilter} setRev={setRev} setVolume={setVolume} setBpm={setBpm} start={start} stop={stop} />
    </div>
  );
};
export default Header;
