import "../App.css";
import Knob from "react-simple-knob";
import useVolume from "../hooks/use-volume";
import * as Tone from "tone";

const knobStyle = {
  margin: "0 auto",
  height: "50px",
  fontFamily: "Arial",
  color: "black",
};

const InstrumentControl = ({ id }) => {
  const { volumeHandler } = useVolume();
  return (
    <div className="knobs">
      <p>{id}</p>
      <button>Mute</button>
      <button>Solo</button>
      <Knob
        name="Volume"
        unit=""
        defaultPercentage={1}
        onChange={volumeHandler}
        bg="black"
        fg="white"
        mouseSpeed={1}
        transform={(p) => parseInt(p * 300)}
        style={knobStyle}
      />
    </div>
  );
};

export default InstrumentControl;
