import "../App.css";
import Knob from "react-simple-knob";
import Slider from 'react-input-slider';
import { useState } from "react";

const knobStyle = {
  margin: "0 auto",
  height: "50px",
  fontFamily: "Arial",
  color: "black",
};

const Master = ({ setVolume, setDist, setRev, setBpm, start, stop }) => {
  const [state, setState] = useState(10);
  return (
    <div className="master-control">
      <span>Master control section</span>
      <hr />
      <button onClick={start}>Play</button>
      <button onClick={stop}>Stop</button>
      <div className="master-knobs">
        <Slider axis="x" x={state.x} onChange={(x) => setState(state => (x))} />
        <Knob
          name="Volume"
          unit=""
          defaultPercentage={1}
          onChange={setVolume}
          bg="black"
          fg="white"
          mouseSpeed={1}
          transform={(p) => parseInt(p * 50, 10) / 50}
          style={knobStyle}
        />
        <Knob
          name="BPM"
          unit=""
          defaultPercentage={0.4}
          onChange={setBpm}
          bg="black"
          fg="white"
          mouseSpeed={1}
          transform={(p) => parseInt(p * 300)}
          style={knobStyle}
        />
        <Knob
          name="Drive"
          unit=""
          defaultPercentage={0}
          onChange={setDist}
          bg="black"
          fg="white"
          mouseSpeed={1}
          transform={(p) => parseInt(p * 50, 10) / 50}
          style={knobStyle}
        />
        <Knob
          name="Reverb"
          unit=""
          defaultPercentage={0}
          onChange={setRev}
          bg="black"
          fg="white"
          mouseSpeed={1}
          transform={(p) => parseInt(p * 50, 10) / 50}
          style={knobStyle}
        />
      </div>
    </div>
  );
};

export default Master;
