import "../App.css";
import Knob from "react-simple-knob";


const knobStyle = {
  margin: "0 auto",
  height: "50px",
  fontFamily: "Arial",
  color: "black",
};

const InstrumentControl = ({ id, volumeHandler, muteHandler }) => {
  const volumeRotary = (level) => {
     volumeHandler(level, id);
  }
  const muteSet = (id) => {
    muteHandler(id);
  }
  return (
    <div className="knobs">
      <p>{id}</p>
      <button onClick={muteSet}>Mute</button>
      <button>Solo</button>
      <Knob
        name="Volume"
        unit=""
        defaultPercentage={1}
        onChange={volumeRotary}
        bg="black"
        fg="white"
        mouseSpeed={1}
        transform={(p) => parseInt(p * -24)}
        style={knobStyle}
      />
    </div>
  );
};

export default InstrumentControl;
