import "../App.css";

import GridCell from "./GridCell";

const Seq = ({
  beatState,
  gridChange,
  gridState,
  playBd,
  playSn,
  playCh,
  playOh,
  playCy,
}) => {
  const { bPat, sPat, hPat, oPat, cPat } = gridState;

  const bGrid = bPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          beatState={beatState}
          active={false}
          key={index}
          id={index}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        beatState={beatState}
        active={true}
        key={index}
        id={index}
        gridChange={gridChange}
      />
    );
  });
  const sGrid = sPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          beatState={beatState}
          active={false}
          key={index}
          id={index + 16}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        beatState={beatState}
        active={true}
        key={index}
        id={index + 16}
        gridChange={gridChange}
      />
    );
  });
  const hGrid = hPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          beatState={beatState}
          active={false}
          key={index}
          id={index + 32}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        beatState={beatState}
        active={true}
        key={index}
        id={index + 32}
        gridChange={gridChange}
      />
    );
  });
  const oGrid = oPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          beatState={beatState}
          active={false}
          key={index}
          id={index + 48}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        beatState={beatState}
        active={true}
        key={index}
        id={index + 48}
        gridChange={gridChange}
      />
    );
  });
  const cGrid = cPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          beatState={beatState}
          active={false}
          key={index}
          id={index + 64}
          gridChange={gridChange}
        />
      );
    }
    return (
      <GridCell
        beatState={beatState}
        active={true}
        key={index}
        id={index + 64}
        gridChange={gridChange}
      />
    );
  });

  return (
    <div className="seq">
      <div className="seq-window">
        <p>seq</p>
        <div className="grid">
          {bGrid}
          {sGrid}
          {hGrid}
          {oGrid}
          {cGrid}
        </div>
      </div>
      <div className="pads">
        <div onMouseDown={playBd}>
          <p>Kick</p>
        </div>
        <div onMouseDown={playSn}>
          <p>Snare</p>
        </div>
        <div onMouseDown={playCh}>
          <p>ClHat</p>
        </div>
        <div onMouseDown={playOh}>
          <p>OpHat</p>
        </div>
        <div onMouseDown={playCy}>
          <p>Cym</p>
        </div>
      </div>
    </div>
  );
};

export default Seq;
