import "../App.css";
import Knob from "react-simple-knob";
import { useState } from "react";

const Master = ({ setVolume, start, stop }) => {
  const [knobState, setKnobState] = useState(1)

  const knobStyle = {
    position: "absolute",
    height: "100px",
    fontFamily: "Arial",
    color: "white",
  };
  return (
    <div className="master-control">
      <span>Master control section</span>
      <hr />
      <button onClick={start}>Play</button>
      <button onClick={stop}>Stop</button>
      <Knob
        name="Volume"
        unit="%"
        defaultPercentage={1}
        onChange={() => {setVolume(); setKnobState()}}
        bg="black"
        fg="white"
        mouseSpeed={3}
        transform={(p) => parseInt(p * 50, 10) / 100}
        style={knobStyle}
      />
      <h3>Master Gain</h3>
      <p>{knobState}</p>
    </div>
  );
};

export default Master;
