import "../App.css";
import Knob from "react-simple-knob";

const Master = ({ setVolume, volume, start, stop }) => {
  const knobStyle = {
    margin: "20%",
    height: "70px",
    fontFamily: "Arial",
    color: "black",
  };
  return (
    <div className="master-control">
      <span>Master control section</span>
      <hr />
      <button onClick={start}>Play</button>
      <button onClick={stop}>Stop</button>
      <Knob
        name="Volume"
        unit=""
        defaultPercentage={1}
        onChange={setVolume}
        bg="black"
        fg="white"
        mouseSpeed={3}
        transform={(p) => parseInt(p * 50, 10) / 50}
        style={knobStyle}
      />
      <Knob
        name="BPM"
        unit=""
        defaultPercentage={1}
        onChange={console.log}
        bg="black"
        fg="white"
        mouseSpeed={3}
        transform={(p) => parseInt(p * 50, 10) / 50}
        style={knobStyle}
      />
    </div>
  );
};

export default Master;
