import "../App.css";
import * as Tone from "tone";
import bd01 from ".././assets/bd01.wav"

const buffer = new Tone.ToneAudioBuffer("https://tonejs.github.io/audio/casio/A1.mp3", () => {
	console.log("loaded");
});
const player = new Tone.Player(bd01).toDestination();


const Seq = () => {
  const playFunc = () => {
    player.start();
  }
  return (
    <div className="seq">
      <div className="seq-window">seq</div>
      <div className="pads">
        <div onClick={playFunc}>
          <p>Kick</p>
        </div>
        <div>
          <p>Snare</p>
        </div>
        <div>
          <p>ClHat</p>
        </div>
        <div>
          <p>OpHat</p>
        </div>
        <div>
          <p>Cym</p>
        </div>
      </div>
    </div>
  );
};

export default Seq;
