import React from "react";

const VolumeContext = React.createContext({
    instrumentLevels: [],
    instrumentMute: [],
    volumeHandler: (level, id) => {},
    muteHandler: (id) => {},
    unmuteHandler: (id) => {},
});

export default VolumeContext;
