import "../App.css";

const GridCell = ({ id, active, gridChange, beatState }) => {
  
  const gridCellHandler = () => {
    gridChange(id);
  };
  return (
    <div className="grid-cell-outer" style={{ backgroundColor: "green"}}>
      <div
        className={active ? "grid-cell-active" : "grid-cell"}
        onClick={gridCellHandler}
      ></div>
    </div>
  );
};

export default GridCell;
