import "../App.css";
import Knob from "react-simple-knob";
import { Instrument } from "tone/build/esm/instrument/Instrument";

const knobStyle = {
  margin: "0 auto",
  height: "50px",
  fontFamily: "Arial",
  color: "black",
};

const InstrumentControl = () => {
  return (
    <>
    <p>instrument.id</p>
    <div className="knobs">
      <button>Mute</button>
      <button>Solo</button>
      <Knob
        name="Volume"
        unit=""
        defaultPercentage={1}
        onChange={console.log}
        bg="black"
        fg="white"
        mouseSpeed={1}
        transform={(p) => parseInt(p * 300)}
        style={knobStyle}
      />
    </div>
    </>
  );
};

export default InstrumentControl;
