import "../App.css";
import * as Tone from "tone";
import bd01 from ".././assets/bd01.wav";
import sn01 from ".././assets/sn01.wav";
import ch01 from ".././assets/ch01.wav";
import oh01 from ".././assets/oh01.wav";
import cy01 from ".././assets/cy01.wav";
import SeqContext from "../store/seq-context";
import { useContext } from "react";

import GridCell from "./GridCell";

const multiPlayer = new Tone.Players({
  bd: bd01,
  sn: sn01,
  ch: ch01,
  oh: oh01,
  cy: cy01,
}).toDestination();

Tone.Transport.scheduleRepeat((time) => {
  // use the callback time to schedule events
  multiPlayer.player("bd").start(time);
}, "8n");

const Seq = () => {
  const seqContext = useContext(SeqContext);
  let bPat = seqContext.bPat;
  let sPat = seqContext.sPat;
  let chPat = seqContext.chPat;
  let ohPat = seqContext.ohPat;
  let cyPat = seqContext.cyPat;
  //
  const gridCellHandler = (cell) => {
    seqContext.bdChange(cell);
  };

  let bGrid = bPat.map((item, index) => {
    if (item === 0) {
      return (
        <GridCell
          active={false}
          gridCellHandler={gridCellHandler.bind(null, item)}
          key={index}
        />
      );
    }
    return (
      <GridCell
        active={true}
        gridCellHandler={gridCellHandler.bind(null, item)}
        key={index}
      />
    );
  });
  let sGrid = sPat.map((item, index) => {
    if (item === 0) {
      return <GridCell key={index} />;
    }
    return <div className="grid-cell-active"></div>;
  });
  let chGrid = chPat.map((item, index) => {
    if (item === 0) {
      return <GridCell key={index} />;
    }
    return <div className="grid-cell-active"></div>;
  });
  let ohGrid = ohPat.map((item, index) => {
    if (item === 0) {
      return <GridCell key={index} />;
    }
    return <div className="grid-cell-active"></div>;
  });
  let cyGrid = cyPat.map((item, index) => {
    if (item === 0) {
      return <GridCell key={index} />;
    }
    return <div className="grid-cell-active"></div>;
  });

  const playBd = () => {
    multiPlayer.player("bd").start();
  };
  const playSn = () => {
    multiPlayer.player("sn").start();
  };
  const playCh = () => {
    multiPlayer.player("ch").start();
  };
  const playOh = () => {
    multiPlayer.player("oh").start();
  };
  const playCy = () => {
    multiPlayer.player("cy").start();
  };
  return (
    <div className="seq">
      <div className="seq-window">
        <p>seq</p>
        <hr />
        <div className="grid">
          {bGrid}
          {sGrid}
          {chGrid}
          {ohGrid}
          {cyGrid}
        </div>
      </div>
      <div className="pads">
        <div onMouseDown={playBd}>
          <p>Kick</p>
        </div>
        <div onMouseDown={playSn}>
          <p>Snare</p>
        </div>
        <div onMouseDown={playCh}>
          <p>ClHat</p>
        </div>
        <div onMouseDown={playOh}>
          <p>OpHat</p>
        </div>
        <div onMouseDown={playCy}>
          <p>Cym</p>
        </div>
      </div>
    </div>
  );
};

export default Seq;
