import "../App.css";
import InstrumentControl from "./InstrumentControl";
import Master from "./Master";
import * as Tone from "tone";
import useVolume from "../hooks/use-volume";
import { useEffect } from "react";

const Header = ({setVolume, setBpm, start, stop}) => {
  const { volumeState, volumeHandler } = useVolume();
  useEffect(() => {
     
  }, [volumeState])
  return (
    <div className="header">
      <div className="sub-header">
        <div className="info">
          <span>Header/info</span>
          <div className="display">
            <span>Info display</span>
          </div>
        </div>
        <div className="inst-sect">
          <InstrumentControl id={0} volumeHandler={volumeHandler}/>
          <InstrumentControl id={1} volumeHandler={volumeHandler}/>
          <InstrumentControl id={2} volumeHandler={volumeHandler}/>
          <InstrumentControl id={3} volumeHandler={volumeHandler}/>
          <InstrumentControl id={4} volumeHandler={volumeHandler}/>
        </div>
      </div>
      <Master setVolume={setVolume} setBpm={setBpm} start={start} stop={stop}/>
    </div>
  );
};
export default Header;
