import "../App.css";
import * as Tone from "tone";
import bd01 from ".././assets/bd01.wav";
import sn01 from ".././assets/sn01.wav";
import ch01 from ".././assets/ch01.wav";
import oh01 from ".././assets/oh01.wav";
import cy01 from ".././assets/cy01.wav";


const bd = new Tone.Player(bd01).toDestination();
const sn = new Tone.Player(sn01).toDestination();
const ch = new Tone.Player(ch01).toDestination();
const oh = new Tone.Player(oh01).toDestination();
const cy = new Tone.Player(cy01).toDestination();


const Seq = () => {
  const playBd = () => {
    bd.start();
  }
  const playSn = () => {
    sn.start();
  }
  const playCh = () => {
    ch.start();
  }
  const playOh = () => {
    oh.start();
  }
  const playCy = () => {
    cy.start();
  }
  return (
    <div className="seq">
      <div className="seq-window">seq</div>
      <div className="pads">
        <div onClick={playBd}>
          <p>Kick</p>
        </div>
        <div onClick={playSn}>
          <p>Snare</p>
        </div>
        <div onClick={playCh}>
          <p>ClHat</p>
        </div>
        <div onClick={playOh}>
          <p>OpHat</p>
        </div>
        <div onClick={playCy}>
          <p>Cym</p>
        </div>
      </div>
    </div>
  );
};

export default Seq;
