import "../App.css";
import * as Tone from "tone";
import bd01 from ".././assets/bd01.wav";
import sn01 from ".././assets/sn01.wav";
import ch01 from ".././assets/ch01.wav";
import oh01 from ".././assets/oh01.wav";
import cy01 from ".././assets/cy01.wav";


const multiPlayer = new Tone.Players({
    bd: bd01,
    sn: sn01,
    ch: ch01,
    oh: oh01,
    cy: cy01,
}).toDestination();

const seq = new Tone.Sequence((time, note) => {
    multiPlayer.player("bd").start(time, 0, time);
	// subdivisions are given as subarrays
}, ["C4", ["E4", "D4", "E4"], "G4", ["A4", "G4"]]).start(0);


const Seq = () => {
  const playBd = () => {
    multiPlayer.player("bd").start();
    Tone.Transport.start();
  }
  const playSn = () => {
    multiPlayer.player("sn").start();
  }
  const playCh = () => {
    multiPlayer.player("ch").start();
  }
  const playOh = () => {
    multiPlayer.player("oh").start();
  }
  const playCy = () => {
    multiPlayer.player("cy").start();
  }
  return (
    <div className="seq">
      <div className="seq-window">seq</div>
      <div className="pads">
        <div onMouseDown={playBd}>
          <p>Kick</p>
        </div>
        <div onMouseDown={playSn}>
          <p>Snare</p>
        </div>
        <div onMouseDown={playCh}>
          <p>ClHat</p>
        </div>
        <div onMouseDown={playOh}>
          <p>OpHat</p>
        </div>
        <div onMouseDown={playCy}>
          <p>Cym</p>
        </div>
      </div>
    </div>
  );
};

export default Seq;
