import "../App.css";
import { useState } from "react";

const GridCell = ({gridCellHandler}) => {
  const [activeState, setActiveState] = useState(false);

  const activateCell = () => setActiveState(!activeState);

  return (
    <div
      onClick={activateCell}
      className={activeState ? "grid-cell-active" : "grid-cell"}
    ></div>
  );
};

export default GridCell;
