import "../App.css";
import Dropdown from "./Dropdown";

const Backdrop = () => {
  return <div className="backdrop"></div>;
};

const ModalOverlay = (props) => {
  return (
    <div className="modal">
      <div>
        <h1>Simple Drum Sequencer</h1>
            <button onClick={props.initialization}>Press to start</button>
            <Dropdown />
      </div>
    </div>
  );
};

const Modal = (props) => {
  return (
    <>
      <Backdrop />
      <ModalOverlay initialization={props.initialization}>{props.children}</ModalOverlay>
    </>
  );
};

export default Modal;
