import "../App.css";
import { useState } from "react";

const GridCell = () => {
    const [activeState, setActiveState] = useState(false)

    const activateCell = () => setActiveState(true);
    return (
        <div onClick={activateCell}className={activeState ? "grid-cell-active" : "grid-cell"}></div>
    )
}

export default GridCell;
